from flask import Flask
from markupsafe import escape
from decorator_fn import bold_text, emphasize_text, underline


app = Flask(__name__)

@app.route('/')
@bold_text
@emphasize_text
@underline
def hello_world():
    return '''<h1 style="text-align: center">hello world!</h1>
                <img src ="data:image/jpeg;base64,">'''

@app.route('/username/<name>/<path:age>')
def welcome(name, age):
    return f'welcome {escape(name)}, you are {age}'

